  mix-blend-mode: overlay;
  border-radius: 24px;
  opacity: 0.8;
  pointer-events: none;
}

.pera-wallet-connect-modal__body__header {
  display: flex;
  align-items: center;
  justify-content: flex-end;
  position: absolute;
  top: -44px;
  right: -44px;
  left: 0;
}

