import React from "react";
import styled from "styled-components";
import tw from "twin.macro";
import { Button } from "../../components/button";
import { Marginer } from "../../components/marginer";
import { useState } from "react";
import algosdk from "algosdk";
import { PeraWalletConnect } from "@perawallet/connect";
import PeraLogo from "../../../assets/images/button-pera-connect@2x.png";
import MyAlgoLogo from "../../../assets/images/myalgo.svg";
import MyAlgoConnect from '@randlabs/myalgo-connect';

const header = header = {
    'X-API-Key': '6RteTejPCj99qAIeQTlFb4nnsCrnDFXp9emi1dtO'
}
const token = ''
const indexerClient = new algosdk.Indexer(token, 'https://mainnet-algorand.api.purestake.io/idx2', header)

const TopSectionContainer = styled.div`
    min-height: 500px
    margin-top: 10em;
    ${tw`
        w-full
        max-w-screen-xl
        flex
        flex-col
        justify-between
        lg:pl-12 
        lg:pr-12
        pl-3
        pr-3
        pt-10

    `};
`;

const Slogan = styled.h1 `
    ${tw`

        text-black
        text-2xl 
        md:text-5xl 
        font-extrabold
        md:font-black
        md:leading-normal  
    `};
`;

// const ButtonsContainer=styled.div`
//     ${tw`
//         flex   
//         mt-4 
//         flex-wrap
//     `};
// `;

const ServicesContainer = styled.div `
    ${tw`
        w-full
        flex
        flex-col
        items-center
    `};
`;

const Title = styled.h1 `
    ${tw`
        text-4xl
        font-extrabold
        text-black
    `};
`;

const ServicesWrapper = styled.div`
    ${tw`
        flex
        flex-wrap
    `}


`;

const CardContainer = styled.div `
    ${tw`
        flex
        flex-col
        overflow-hidden
        mt-0.5 
        mb-5
        mr-2
        ml-9
    `};
    width: 440px;
    height: 560px;
    background-color: #fff;
    box-shadow: 0 0 3.9px rgba(0, 0 ,0, 0.27);
    border-bottom: 3px solid rgba(0, 0, 255, 1);
    border-top: 3px solid rgba(0, 0, 255, 1);
    border-left: 3px solid rgba(0, 0, 255, 1);
    border-right: 3px solid rgba(0, 0, 255, 1);
`;

const TopContainer = styled.div `    
    ${tw`
        w-full
    `};
`;

const ServiceThumbnail = styled.div `
    width: 100%;
    height: 20em;

    img {
        width: 100%;
        height: 100%;
    }
`;

const ContentContainer = styled.div `
    ${tw`
        w-full
        flex
        flex-col
        items-center
        justify-between
        pr-4
        pl-4
        pt-1
    `};

`;

const BottomContainer = styled.div `
    ${tw`

        w-full
        flex
        flex-col
        items-center
        justify-between
        pt-6
        pr-4 
        pl-4 
    `};

`;

const Title2 = styled.h2 `
    font-size: 20px;
    font-weight: 500;
    ${tw`
        text-black
        text-base
    `};
`;

const Title3 = styled.h2 `
    font-size: 20px;
    font-weight: 500;
    ${tw`
        text-red-900
        text-lg
    `};
`;

const SpecialistName = styled.h4 `

    ${tw`
        text-black
        text-base
        items-center
    `};
    font-size: 20px;


`;

const RatingContainer = styled.div `
    display: flex;
    color: #000;
`;

const PriceContainer = styled.div `
    ${tw`
        flex
        items-center
    `};
`;

const PriceText = styled.div `
    ${tw`
    `};
    color: #2A9D8F;
    font-weight: 500;
`;

const StartingAtText = styled.h6 `
    color: rgba(0, 1, 100, 0.5);
    font-weight: 400;
`;

const ButtonsContainer=styled.div`
    width: 200px;
    ${tw`
        flex    
        flex-wrap
        h-full
    `};
`;

const FooterContainer = styled.div `
    height:100%;
    ${tw`
        w-full
        flex
        flex-row
        items-center
        pl-96


    `};

`;

const Input = styled.input.attrs(props => ({
    type: "number",
  }))`
    border: 2px solid black;
    width: 50px;
    height: 40px;
    ${tw`
        text-4xl

    `};
`;



export function TopSection(props) {

    const [apartmentID, setApartmentId] = useState('');



    const [apartments, setApartments] = useState([]);
    const [mapping, setMapping] = useState('not')

    const [accountAddress, setAccountAddress] = useState(null);
    const isConnectedToPeraWallet = !!accountAddress;
    useEffect(() => {
      // Reconnect to the session when the component is mounted
      peraWallet
        .reconnectSession()
        .then((accounts) => {
          peraWallet.connector.on("disconnect", handleDisconnectWalletClick);

          if (accounts.length) {
            setAccountAddress(accounts[0]);
          }
        })
        .catch((e) => console.log(e));
    }, []);

    function handleConnectWalletClick() {
        peraWallet
          .connect()
          .then((newAccounts) => {
            peraWallet.connector.on("disconnect", handleDisconnectWalletClick);

            setAccountAddress(newAccounts[0]);
          })
          .catch((error) => {
            if (error?.data?.type !== "CONNECT_MODAL_CLOSED") {
              console.log(error);
            }
          });
      }

    function handleDisconnectWalletClick() {
        peraWallet.disconnect();

        setAccountAddress(null);
    }

    async function connectToMyAlgo() {
        try {
          const accounts = await myAlgoWallet.connect();
          const addresses = accounts.map(account => account.address);

        } catch (err) {
          console.error(err);
        }
      }
    if ( mapping === 'not') { return <ServicesContainer>You haven't burnt any BrokeBoiz yet!</ServicesContainer>}

    return (
            <TopSectionContainer>
                <Marginer direction="vertical" margin="1.5em"/>
                <Slogan>Resurrect your Burnt OGs!</Slogan>
                <Marginer direction="vertical" margin="5em" />
                <ServicesContainer>
                    <Title>Current burnt BrokeBoiz</Title>
                    <Marginer direction="vertical" margin="3em"/>                           
                        <ServicesWrapper >
                        {apartments.map((apartment) => (
                            apartment.map((x) => (            
                            <CardContainer key={x.apartmentsId.toString()} >
                                <TopContainer >
                                    <ServiceThumbnail >
                                        <img src={x.imageLink} alt=" " />
                                    </ServiceThumbnail>
                                </TopContainer>
                                <ContentContainer>
