import React from "react";
import styled from "styled-components";
import tw from "twin.macro";
import { Button } from "../../components/button";
import { Marginer } from "../../components/marginer";
import TopSectionImg from "../../../assets/images/home2.jpeg"

const TopSectionContainer = styled.div`
    min-height: 500px
    margin-top: 10em;
    ${tw`
        w-full
        max-w-screen-xl
        flex
        flex-col
        justify-between
        lg:pl-12 
        lg:pr-12
        pl-3
        pr-3
        pt-10

    `};
`;

const LeftContainer = styled.div`
    ${tw`
        w-1/2
        flex
        items-start
        justify-between
        flex-col
        pl-16
    `};
`;


const Slogan = styled.h1 `
    ${tw`

        text-black
        text-2xl 
        md:text-5xl 
        font-extrabold
        md:font-black
        md:leading-normal  
    `};
`;

const Description = styled.p`
    ${tw`

        max-w-2xl 
        text-sm
        text-gray-500 
        md:text-base
        font-normal
        mt-0.5
    `}
`;

const StandaloneApartment = styled.div`
width:40em;
max-height: 30em;
position: absolute;
top: 10em;
right: 5em;
z-index: -1;


img {
    width: 100%;
    height: auto;
    max-height: max-content;
}

`;

const ButtonsContainer=styled.div`
    ${tw`
        flex   
        mt-4 
        flex-wrap
    `};
`;



export function TopSection(props) {



    return (
            <TopSectionContainer>
                <LeftContainer>
                    <Marginer direction="vertical" margin="1.5em"/>
                    <Slogan>Fuck Caesar!</Slogan>
                    <Description>
                        Ressurect and upgrade your OG BrokeBoiz
                    </Description>
                    <ButtonsContainer onClick={console.log(0)} > 
                        {<Button text="Resurrect Your BBs" />}
                    </ButtonsContainer> 
                    <ButtonsContainer onClick={console.log(0) }> 
                        <Button text="Upgrade Your BBs" />                    
                    </ButtonsContainer>           
                </LeftContainer>
                <StandaloneApartment>
                    <img src={TopSectionImg} alt="" />
                </StandaloneApartment>
                <Marginer direction="vertical" margin="5em" />
            </TopSectionContainer>
    );
}
